"use client";
import styles from "./styles.module.css";
// import type { Metadata } from "next";
import { getPosts } from "@/service/getPost";
import Poster from "@/components/Post/Poster";
import Search from "@/components/Search/Search";
import { useEffect, useState } from "react";

export interface IPoste {
  userId: string;
  id: string;
  title: string;
  body: string;
}
// export const metadata: Metadata = {
//   title: "About",
//   description: "Generated by create next app",
//   keywords: "домашняя страница",
// };

export default function About() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getPosts()
      .then(setPosts)
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className={styles.container}>
      <Search setPosts={setPosts} />
      <ol className={styles.ul}>
        {!loading ? (
          posts.map((post: IPoste) => {
            return <Poster key={post.id} id={post.id} title={post.title} />;
          })
        ) : (
          <div>loading...</div>
        )}
      </ol>
    </div>
  );
}
